import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Qualifier;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.Pageable;
import org.springframework.web.bind.annotation.PathVariable;
import org.springframework.web.bind.annotation.RequestBody;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestMethod;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.bind.annotation.RestController;
import com.wavemaker.runtime.data.exception.EntityNotFoundException;
import com.wavemaker.runtime.data.export.ExportType;
import com.wavemaker.runtime.data.expression.QueryFilter;
import com.wavemaker.runtime.file.model.Downloadable;
import com.wordnik.swagger.annotations.Api;
import com.wordnik.swagger.annotations.ApiOperation;
import com.testing26thjuly_.wmstudio.Login;
import com.testing26thjuly_.wmstudio.service.LoginService;
import com.wordnik.swagger.annotations.*;
import com.wavemaker.tools.api.core.annotations.WMAccessVisibility;
import com.wavemaker.tools.api.core.models.AccessSpecifier;

/**
 * Controller object for domain model class Login.
 * @see Login
 */
@RestController("WMSTUDIO.LoginController")
@RequestMapping("/WMSTUDIO/Login")
@Api(description = "Exposes APIs to work with Login resource.", value = "LoginController")
public class LoginController {

    private static final Logger LOGGER = LoggerFactory.getLogger(LoginController.class);

    @Autowired
    @Qualifier("WMSTUDIO.LoginService")
    private LoginService loginService;

    /**
     * @deprecated Use {@link #findLogins(String)} instead.
     */
    @Deprecated
    @RequestMapping(value = "/search", method = RequestMethod.POST)
    @ApiOperation(value = "Returns the list of Login instances matching the search criteria.")
    public Page<Login> findLogins(Pageable pageable, @RequestBody QueryFilter[] queryFilters) {
        LOGGER.debug("Rendering Logins list");
        return loginService.findAll(queryFilters, pageable);
    }

    @RequestMapping(method = RequestMethod.GET)
    @ApiOperation(value = "Returns the list of Login instances matching the search criteria.")
    public Page<Login> findLogins(@RequestParam(value = "q", required = false) String query, Pageable pageable) {
        LOGGER.debug("Rendering Logins list");
        return loginService.findAll(query, pageable);
    }

    @RequestMapping(value = "/export/{exportType}", method = RequestMethod.GET, produces = "application/octet-stream")
    @ApiOperation(value = "Returns downloadable file for the data.")
    public Downloadable exportLogins(@PathVariable("exportType") ExportType exportType, @RequestParam(value = "q", required = false) String query, Pageable pageable) {
        return loginService.export(exportType, query, pageable);
    }

    /**
	 * This setter method should only be used by unit tests
	 *
	 * @param service LoginService instance
	 */
    protected void setLoginService(LoginService service) {
        this.loginService = service;
    }

    @RequestMapping(method = RequestMethod.POST)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    @ApiOperation(value = "Creates a new Login instance.")
    public Login createLogin(@RequestBody Login login) {
        LOGGER.debug("Create Login with information: {}", login);
        login = loginService.create(login);
        LOGGER.debug("Created Login with information: {}", login);
        return login;
    }

    @RequestMapping(value = "/count", method = RequestMethod.GET)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    @ApiOperation(value = "Returns the total count of Login instances.")
    public Long countLogins(@RequestParam(value = "q", required = false) String query) {
        LOGGER.debug("counting Logins");
        return loginService.count(query);
    }

    @RequestMapping(value = "/{id}", method = RequestMethod.GET)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    @ApiOperation(value = "Returns the Login instance associated with the given id.")
    public Login getLogin(@PathVariable(value = "id") Integer id) throws EntityNotFoundException {
        LOGGER.debug("Getting Login with id: {}", id);
        Login foundLogin = loginService.getById(id);
        LOGGER.debug("Login details with id: {}", foundLogin);
        return foundLogin;
    }

    @RequestMapping(value = "/{id}", method = RequestMethod.PUT)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    @ApiOperation(value = "Updates the Login instance associated with the given id.")
    public Login editLogin(@PathVariable(value = "id") Integer id, @RequestBody Login login) throws EntityNotFoundException {
        LOGGER.debug("Editing Login with id: {}", login.getId());
        login.setId(id);
        login = loginService.update(login);
        LOGGER.debug("Login details with id: {}", login);
        return login;
    }

    @RequestMapping(value = "/{id}", method = RequestMethod.DELETE)
    @WMAccessVisibility(value = AccessSpecifier.APP_ONLY)
    @ApiOperation(value = "Deletes the Login instance associated with the given id.")
    public boolean deleteLogin(@PathVariable(value = "id") Integer id) throws EntityNotFoundException {
        LOGGER.debug("Deleting Login with id: {}", id);
        Login deletedLogin = loginService.delete(id);
        return deletedLogin != null;
    }
}
